let UpperEnd=datetime('2025-01-10');
let WindowSize='31d';
let BinSize='1d';
//{
 Stock
  | where valueTimestamp between (UpperEnd - totimespan(WindowSize) .. UpperEnd)
  | where Symbol == 'MSFT'                                                             // Demo purose
    //
    // Generate a data point for every day and fill it forward (data contain empty spots, e.g. over the weekend)
    | make-series close = take_any(close) default =real(null), volume = (take_any(volume)) default=int(null) on valueTimestamp step 1d by Symbol
    | extend close = series_fill_forward(close), volume = series_fill_const(volume,0)  
    | mv-expand close, volume, valueTimestamp
    | extend close=toreal(close), volume=toint(volume), valueTimestamp=todatetime(valueTimestamp)
    | render linechart with(ycolumns=close, xcolumn=valueTimestamp, series=Symbol)
//}


// 02: Create the buckets
let UpperEnd=datetime('2025-01-10');
let WindowSize='31d';
let BinSize='4d';
  Stock
  | where valueTimestamp between (UpperEnd - totimespan(WindowSize) .. UpperEnd)
  | where Symbol == 'MSFT'                                                             // Demo purose
    //
    // Generate a data point for every day and fill it forward (data contain empty spots, e.g. over the weekend)
    | make-series close = take_any(close) default =real(null), volume = (take_any(volume)) default=int(null) on valueTimestamp step 1d by Symbol
    | extend close = series_fill_forward(close), volume = series_fill_const(volume,0)  
    | mv-expand close, volume, valueTimestamp
    | extend close=toreal(close), volume=toint(volume), valueTimestamp=todatetime(valueTimestamp)
    //
    // Create buckets
    | summarize  arg_max_Ts_close = arg_max(valueTimestamp, close)
               , arg_min_Ts_close = arg_min(valueTimestamp, close)
               , minClose=min(close)
               , avgClose=avg(close)
               , maxClose=max(close)
               , volume = sum(volume) 
                  by bin(valueTimestamp, totimespan(BinSize)), Symbol
    | render linechart with(ycolumns=close, xcolumn=arg_max_Ts_close, series=Symbol)
}



GetStock(now(),'120d','7d')

GetStock(now(), '720d', '5d')
| where Symbol in ('MSFT', 'NVDA')
| render linechart with(ycolumns=close, xcolumn=valueTimestamp, series=Symbol)


Stock
| distinct Symbol

